// To fake loading times
const delay = (timeout: number) => new Promise((resolve) => setTimeout(resolve, timeout));

export const mockElectionAPI = makeElectionApi({
  fetch: mockFetch([
    [
      "GET",
      "/ballot",
      (async ({ query: { BallotId, Division, CountyId, LocalityId } }) => {
        await delay(1000);
        const scope = {
          type: Division,
          countyId: CountyId,
          localityId: Division !== "diaspora_country" ? LocalityId : undefined,
          countryId: Division === "diaspora_country" ? LocalityId : undefined,
          countyName: "Prahova",
          localityName: "Ploiești",
          countryName: "Spania",
        };
        return {
          id: BallotId,
          scope,
          meta: mockPresidentialElectionMeta,
          turnout: mockPresidentialElectionTurnout,
          observation: mockObservation,
          results: mockResults,
        };
      }) as APIMockHandler<ElectionBallot, unknown, any>, // eslint-disable-line @typescript-eslint/no-explicit-any
    ],

    [
      "GET",
      "/ballots",
      (async () => {
        await delay(1000);
        return mockElectionList;
      }) as APIMockHandler<ElectionBallotMeta[]>,
    ],

    [
      "GET",
      "/counties",
      (async () => {
        await delay(1000);
        return [
          { id: 0, name: "Prahova" },
          { id: 1, name: "Buzău" },
          { id: 2, name: "Brașov" },
          { id: 3, name: "Constanța" },
        ];
      }) as APIMockHandler<{ id: number; name: string }[]>,
    ],

    [
      "GET",
      "/localities",
      (async () => {
        await delay(1000);
        return [
          { id: 0, name: "Văleni de Munte" },
          { id: 1, name: "Ploiești" },
          { id: 2, name: "Slănic" },
          { id: 3, name: "Câmpina" },
        ];
      }) as APIMockHandler<{ id: number; name: string }[]>,
    ],

    [
      "GET",
      "/countries",
      (async () => {
        await delay(1000);
        return [
          { id: 1, name: "Portugalia" },
          { id: 2, name: "Spania" },
          { id: 3, name: "Regatul Unit al Marii Britanii și Irlandei de Nord" },
        ];
      }) as APIMockHandler<{ id: number; name: string }[]>,
    ],
  ]),
});
